const fs = require('fs')

function doIt() {

    const moduleDir = __dirname + '/..'

    fs.readdir(moduleDir, (error, files) => {
        if (error) {
            throw error;
        }

        // list all files
        files.forEach(file => {
            if (file.match(/^testing-vbank-js-launcher-\d.*\.tgz$/)) {
                fs.unlink(file, (err) => {
                    if (err) throw err;
                    console.log(file + ' was deleted!');
                });
            }
        });
    });

}

doIt();