function getPlatform() {
    const platform = process.platform;
    const arch = process.arch;

    const platforms = {
        darwin: (arch) => arch === 'arm64' ? 'mac-arm64' : 'mac-x64',
        linux: () => 'linux64',
        win32: (arch) => arch === 'x64' ? 'win64' : 'win32'
    };

    return platforms[platform] && platforms[platform](arch);
}

module.exports = { getPlatform }